viewof visible_topics = Inputs.checkbox(
  topic_names,
  { value: topic_names,
    label: "Topics:"
  }
)







PlotData



Plot.plot({
  grid: true,
  color: {
    scheme: "spectral",
    domain: topic_names,
    legend: true
  },
  x: {
    label: ""
  },
  y: {
    label: ""
  },
  marks: [
    Plot.dot(
        filtered,
        {
            x: "embedding.x",
            y: "embedding.y",
            title: (d) =&gt;
        `${d.doi}\n ${d.title}\n ${d['journal.abbreviation']} ${d['journal.volume']} (${d['year']})`,
            fill: "embedding.topic_name",
            r: 6,
        }
    )
  ],
  tooltip: {
    stroke: "blue",
  }
})









Inputs.table(filtered, {
  columns: [
    "doi",
    "title",
    "journal.name",
    "journal.volume",
    "year",
  ],
  header: {
    "journal.name": "journal",
    "journal.volume": "volume",
  },
  format: {
    doi: doi =&gt; htl.html`&lt;a href=https://doi.org/${doi} target=_blank&gt;${doi}&lt;/a&gt;`,
    title: title =&gt; htl.html`${title}`,
    "journal.volume": volume =&gt; htl.html`&lt;b&gt;${volume}&lt;/b&gt;`,
    year: year =&gt; htl.html`${year}`,
  },
  width: {
    "title": 10
  },
  layout: "fixed",
})










import {Plot} from "@mkfreeman/plot-tooltip"
papers = FileAttachment("data/all_papers.csv").csv({ typed: true })

topic_names = Array.from(new Set(papers.map(d =&gt; d["embedding.topic_name"])))

filtered = papers.filter(function(p) {
  return visible_topics.includes(p["embedding.topic_name"]);
})



































Note



This represents a two-dimensional embedding of all the group’s publications, clustered in topics. Publication abstracts are lemmatized and clustered using BERTopic with a UMAP dimensionality reduction.
For a more detailed description of our research, see Topics.

Publications

import {Plot} from "@mkfreeman/plot-tooltip"
import {map} from "@martien/ramda"

papers_r = FileAttachment("../data/all_papers.json").json()
n_columns = Object.entries(papers_r["doi"]).length
index_array = Array.from({length: n_columns}, (_, i) =&gt; i)
papers = map(i =&gt; map(x =&gt; x[i], papers_r), index_array)

my_papers = papers.filter(function(p) {
    return p.authors.map(a =&gt; a.full_name).includes("Kiran Kanekal")
})

Inputs.table(my_papers, {
    columns: [
    "doi",
    "title",
    "journal.name",
    "journal.volume",
    "journal.issue",
    "journal.pages",
    "year",
    "embedding.topic_name"
  ],
  header: {
    "journal.name": "journal",
    "journal.volume": "volume",
    "journal.issue": "issue",
    "journal.pages": "pages",
    "embedding.topic_name": "topic",
  },
  format: {
    doi: doi =&gt; htl.html`&lt;a href=https://doi.org/${doi} target=_blank&gt;${doi}&lt;/a&gt;`,
    title: title =&gt; htl.html`${title}`,
    "journal.volume": volume =&gt; htl.html`&lt;b&gt;${volume}&lt;/b&gt;`,
    year: year =&gt; htl.html`${year}`,
  },
  width: {
    "title": 10
  },
  layout: "auto",
})

Collaborators

collaborators = Array.from(
    new Set(
        my_papers.flatMap(p =&gt; p.authors)
        .filter(function(a) {
            return a.full_name != "Kiran Kanekal"
            }
        )
    )
)

list_of_collaborators = (
    Array.from(d3.group(collaborators, a =&gt; a.full_name).keys())
)
dict_of_collaborators = (
    collaborators.reduce(
        (acc, x) =&gt; (
            {...acc, [x.full_name]:
                {
                    "given": x.given,
                    "family": x.family,
                    "affiliation_city": x.affiliation.city,
                    "affiliation_name": x.affiliation.name,
                    "affiliation_country": x.affiliation.country,
                    "orcid": x.orcid,
                }
            }
        ),
        {}
    )
)

Inputs.table(
    list_of_collaborators.map(c =&gt; dict_of_collaborators[c]),
    {
        columns: [
            "given",
            "family",
            "orcid",
            "affiliation_country",
            "affiliation_city",
            "affiliation_name",
        ],
        header: {
            "affiliation_country": "country",
            "affiliation_city": "city",
            "affiliation_name": "affiliation",
        },
        multiple: false,
        sort: "family",
        layout: "fixed",
        format: {
            orcid: orcid =&gt;
            htl.html`&lt;a href=https://orcid.org/${orcid} target=_blank&gt;${orcid}&lt;/a&gt;`
        },
    }
)

